00361     public static bool IsValidUrl(string url,string patron)
00362     {
00363         if (string.IsNullOrEmpty(url))
00364             return false;//Uso Regex para generar un patron de url
00365        
00366         return Regex.IsMatch(url, patron);
00367     }
00374     public static string GenerarWikiDataURL(string url, string animal)
00375     {
00376         // Buscar si el código de la entidad empieza por 'Q' y extraerlo
00377         var match = Regex.Match(url, @"Q\d+");
00378         if (match.Success)
00379         {
00380             return $"https://www.wikidata.org/wiki/{match.Value}";
00381         }
00382         else
00383         {
00384             Console.WriteLine($"No se pudo corregir la URL de Wikidata para {animal}");
00385             return $"https://www.wikidata.org/wiki/Q";
00386         }
00387     }
