00395         public static List<RegistroAnimal> NormalizacionAnimales(List<RegistroAnimal> records)
00396     {
00397         var animalSynonyms = new Dictionary<string, string>(StringComparer.OrdinalIgnoreCase)
00398         {
00399             { "burro", "asno" },  
00400             { "asno", "asno" },
00401             {"camaleón","camaleon" }
00402             
00403         };
00404         var registrosNormalizados = new List<RegistroAnimal>();
00405 
00406         
00407         foreach (var record in records)
00408         {
00409             if (record.Animal != null && animalSynonyms.ContainsKey(record.Animal))
00410             {                
00411                 record.Animal = animalSynonyms[record.Animal];
00412             }
00413                         
00414             registrosNormalizados.Add(record);
00415         }
00416         return registrosNormalizados;
00417 
